import React, { useState } from "react";

function App() {
  const [input, setInput] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);

  const getRecommendations = async () => {
    if (!input) {
      alert("Please enter movie preference");
      return;
    }

    setLoading(true);

    try {
      const response = await fetch("http://localhost:5000/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          user_input: input
        })
      });

      const data = await response.json();
      setMovies(data.recommended_movies || []);
    } catch (error) {
      alert("Backend not running");
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: "40px", fontFamily: "Arial" }}>
      <h2>ðŸŽ¬ Movie Recommendation App</h2>

      <input
        type="text"
        placeholder="Enter movie preference (e.g. action, romance)"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        style={{ padding: "10px", width: "300px" }}
      />

      <br /><br />

      <button onClick={getRecommendations} style={{ padding: "10px 20px" }}>
        Get Recommendations
      </button>

      <br /><br />

      {loading && <p>Loading...</p>}

      <ul>
        {movies.map((movie, index) => (
          <li key={index}>{movie}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
